BEGIN BATCH
	INSERT INTO auth.oauth_access_token (oauth_token, access_token, authentication, last_modified) VALUES (?, ?, ?, ?);
	INSERT INTO auth.oauth_access_token_by_authentication (authentication_id, access_token, authentication, last_modified) VALUES (?, ?, ?, ?);
	INSERT INTO auth.oauth_access_token_by_client_with_token (client_id, client_mod, user_id, oauth_token, access_token, authentication, last_modified) VALUES (?, ?, ?, ?, ?, ?, ?);
	INSERT INTO auth.oauth_access_token_by_user (user_id_hash, access_token, authentication, device_name) VALUES (?, ?, ?, ?);
	INSERT INTO auth.oauth_access_token_by_user_and_client (user_id_hash, client_id, access_token, authentication, device_name) VALUES (?, ?, ?, ?, ?);
APPLY BATCH;
